CREATE TABLE IF NOT EXISTS "ability_score" (
    "id" VARCHAR, /* Uppercase */
    "name" VARCHAR NOT NULL,
    "description" VARCHAR NOT NULL,
    PRIMARY KEY ("id")
);


CREATE TABLE IF NOT EXISTS "rules_source" (
    "id" VARCHAR, /* Uppercase */
    "name" VARCHAR NOT NULL,
    "url" VARCHAR NOT NULL,
    PRIMARY KEY ("id")
);


CREATE TABLE IF NOT EXISTS "creature_size" (
    "id" VARCHAR, /* Uppercase */
    "name" VARCHAR NOT NULL,
    "space" VARCHAR NOT NULL,
    "reach_tall" VARCHAR NOT NULL,
    "reach_long" VARCHAR NOT NULL,
    PRIMARY KEY ("id")
);


CREATE TABLE IF NOT EXISTS "language_category" (
    "id" VARCHAR, /* Uppercase */
    "name" VARCHAR NOT NULL,
    PRIMARY KEY ("id")
);

CREATE TABLE IF NOT EXISTS "language" (
    "id" VARCHAR, /* Uppercase */
    "name" VARCHAR NOT NULL,
    "spoken_by" VARCHAR NOT NULL,
    "category_id" VARCHAR NOT NULL,
    FOREIGN KEY ("category_id") REFERENCES "language_category"("id"),
    PRIMARY KEY ("id")
);


CREATE TABLE IF NOT EXISTS "special" (
    "id" VARCHAR, /* Uppercase */
    "category" VARCHAR, /* Uppercase */
    "name" VARCHAR NOT NULL,
	"description" VARCHAR NOT NULL,
	"level"	SMALLINT,
	"actions" VARCHAR,
	"requirements" VARCHAR,
    "src" VARCHAR NOT NULL,
    "url" VARCHAR,
    FOREIGN KEY ("src") REFERENCES "rules_source"("id"),
	PRIMARY KEY("id", "category")
);
